update t_fusionpublipostage set requetesql = 'select coalesce((select max(t_specialists.dr_titre) from t_operateurs  inner join t_specialists on t_operateurs.id_user = t_specialists.id_user where t_horrdvpat.id_horrdvpat = t_operateurs.id_horrdvpat), (select max(t_specialists.dr_titre) from t_patients inner join t_specialists on t_patients.id_specialist = t_specialists.id_specialist where t_horrdvpat.id_patient = t_patients.id_patient), '''') from t_horrdvpat' where champfusion like '%Spc_titre_rdv%';
update t_fusionpublipostage set requetesql = 'select coalesce((select max(t_specialists.dr_nom) from t_operateurs  inner join t_specialists on t_operateurs.id_user = t_specialists.id_user where t_horrdvpat.id_horrdvpat = t_operateurs.id_horrdvpat), (select max(t_specialists.dr_nom) from t_patients inner join t_specialists on t_patients.id_specialist = t_specialists.id_specialist where t_horrdvpat.id_patient = t_patients.id_patient), '''') from t_horrdvpat' where champfusion like '%Spc_nom_rdv%';
update t_fusionpublipostage set requetesql = 'select coalesce((select max(t_specialists.dr_prenom) from t_operateurs  inner join t_specialists on t_operateurs.id_user = t_specialists.id_user where t_horrdvpat.id_horrdvpat = t_operateurs.id_horrdvpat), (select max(t_specialists.dr_prenom) from t_patients inner join t_specialists on t_patients.id_specialist = t_specialists.id_specialist where t_horrdvpat.id_patient = t_patients.id_patient), '''') from t_horrdvpat' where champfusion like '%Spc_prenom_rdv%';
update t_fusionpublipostage set requetesql = 'select coalesce((select max(t_specialists.init) from t_operateurs  inner join t_specialists on t_operateurs.id_user = t_specialists.id_user where t_horrdvpat.id_horrdvpat = t_operateurs.id_horrdvpat), (select max(t_specialists.init) from t_patients inner join t_specialists on t_patients.id_specialist = t_specialists.id_specialist where t_horrdvpat.id_patient = t_patients.id_patient), '''') from t_horrdvpat' where champfusion like '%Spc_initiale_rdv%';
